import React from "react";

const Navbar = () => {
  const [activeLink, setActiveLink] = useState("");

  const handleLinkHover = (link) => {
    setActiveLink(link);
  };

  const handleLinkLeave = () => {
    setActiveLink("");
  };

  return (
    <nav className="bg-gray-800 py-4">
      <div className="container mx-auto flex justify-between items-center">
        <div className="text-white font-bold">Logo</div>
        <div className="flex space-x-6">
          <a
            href="#"
            className={`text-white hover:text-yellow-500 transition duration-300 relative ${
              activeLink === "Home" ? "text-yellow-500" : ""
            }`}
            onMouseEnter={() => handleLinkHover("Home")}
            onMouseLeave={handleLinkLeave}
          >
            Home
            {activeLink === "Home" && (
              <span className="absolute bottom-0 left-0 w-full h-0.5 bg-yellow-500"></span>
            )}
          </a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
