import React from "react";

const feature = [
  {
    icon: "icon1.svg",
    title: "Cost Savings",
    description:
      "An electronic bill of lading could save $6B billion in direct costs and enable $40 billion in global trade (McKinsey).",
  },
  // Add the rest of the features data here
];

const Advantage = () => {
  return (
    <div className=" my-24">
      <div className="flex flex-col items-center justify-center">
        <p className=" font-semibold  text-[40px] text-[#163F5B] ">
          The DocuTrade Advantage
        </p>
        <div className="container grid grid-cols-4 grid-rows-2">
          {features.map((feature, index) => (
            <div key={index} className="bg-blue-900 p-6 rounded-lg">
              <img
                src={feature.icon}
                alt={feature.title}
                className="w-12 h-12 mb-4"
              />
              <h3 className="text-lg font-semibold">{feature.title}</h3>
              <p className="text-gray-300">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Advantage;
